```js
class InteractiveQuiz {
  constructor(config) {
    // Set up default config options
    this.config = Object.assign(
      {
        shuffleQuestions: false,
        autoProgress: false
      },
      config
    );

    // Initialize quiz state
    this.currentQuestion = 0;
    this.correctAnswers = 0;
    this.isComplete = false;

    // Get DOM elements for quiz components
    this.quizContainer = document.querySelector("#quiz-container");
    this.quizTitle = document.querySelector("#quiz-title");
    this.quizQuestions = document.querySelector("#quiz-questions");
    this.quizControls = document.querySelector("#quiz-controls");
    this.quizPrev = document.querySelector("#quiz-prev");
    this.quizNext = document.querySelector("#quiz-next");
    this.quizSubmit = document.querySelector("#quiz-submit");
    this.quizResults = document.querySelector("#quiz-results");

    // Bind event listeners
    this.quizPrev.addEventListener("click", this.handlePrevClick.bind(this));
    this.quizNext.addEventListener("click", this.handleNextClick.bind(this));
    this.quizSubmit.addEventListener(
      "click",
      this.handleSubmitClick.bind(this)
    );
  }

  async loadQuizData() {
    // Load quiz data from external source (e.g. API)
    try {
      const response = await fetch("quiz-data.json");
      const data = await response.json();
      this.quizData = data;
    } catch (err) {
      console.error(err);
      // Handle error loading data
    }
  }

  startQuiz() {
    // Load quiz data
    this.loadQuizData().then(() => {
      // Shuffle questions if configured to do so
      if (this.config.shuffleQuestions) {
        this.quizData = this.shuffleArray(this.quizData);
      }

      // Show first question
      this.showQuestion(this.currentQuestion);

      // Show quiz controls
      this.quizControls.style.display = "block";
    });
  }

  showQuestion(index) {
    // Get question data for current index
    const question = this.quizData[index];

    // Set quiz title to current question number and text
    this.quizTitle.textContent = `Question ${index + 1}: ${question.questionText}`;

    // Create HTML for question options
    let optionsHtml = "";
    question.options.forEach((option, index) => {
      optionsHtml += `
        <div class="quiz-option">
          <input type="radio" id="option-${index}" name="quiz-option" value="${option}">
          <label for="option-${index}">${option}</label>
        </div>
      `;
    });

    // Set quiz questions HTML to current question
    this.quizQuestions.innerHTML = `
      <div class="quiz-question">
        <p>${question.questionText}</p>
        ${optionsHtml}
      </div>
    `;

    // Disable previous button if on first question
    if (this.currentQuestion === 0) {
      this.quizPrev.disabled = true;
    } else {
      this.quizPrev.disabled = false;
    }

    // Hide submit button if not on last question
    if (this.currentQuestion < this.quizData.length - 1) {
      this.quizSubmit.style.display = "none";
    } else {
      this.quizSubmit.style.display = "block";
    }
  }

  handlePrevClick() {
    // Decrement current question index and show previous question
    this.currentQuestion--;
    this.showQuestion(this.currentQuestion);
  }

  handleNextClick() {
    // Check answer for current question
    const selectedOption = document.querySelector(
      'input[name="quiz-option"]:checked'
    );
    if (selectedOption) {
      const answer = selectedOption.value;
      const question = this.quizData[this.currentQuestion];
      if (answer === question.correctAnswer) {
        this.correctAnswers++;
      }
      selectedOption.checked = false;
    }

    // Increment current question index and show next question
    this.currentQuestion++;
    if (this.currentQuestion < this.quizData.length) {
      this.showQuestion(this.currentQuestion);
    } else {
      // Quiz is complete
      this.isComplete = true;
      this.showResults();
    }

    // Auto-progress to next question if configured to do so and answer is correct
    if (this.config.autoProgress && answer === question.correctAnswer) {
      this.handleNextClick();
    }
  }

  handleSubmitClick() {
    // Check answer for current question
    const selectedOption = document.querySelector(
      'input[name="quiz-option"]:checked'
    );
    if (selectedOption) {
      const answer = selectedOption.value;
      const question = this.quizData[this.currentQuestion];
      if (answer === question.correctAnswer) {
        this.correctAnswers++;
      }
      selectedOption.checked = false;
    }

    // Quiz is complete
    this.isComplete = true;
    this.showResults();
  }

  showResults() {
    // Hide quiz controls and questions
    this.quizControls.style.display = "none";
    this.quizQuestions.style.display = "none";

    // Show results summary
    this.quizResults.style.display = "block";
    this.quizResults.textContent = `You got ${this.correctAnswers} out of ${
      this.quizData.length
    } questions correct!`;
  }

  shuffleArray(array) {
    // Shuffle array in place using Fisher-Yates algorithm
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }

  testQuiz(answers) {
    // Simulate quiz interaction with provided answers
    this.loadQuizData().then(() => {
      // Shuffle questions if configured to do so
      if (this.config.shuffleQuestions) {
        this.quizData = this.shuffleArray(this.quizData);
      }

      // Iterate over questions, checking provided answers
      answers.forEach((answer, index) => {
        const question = this.quizData[index];
        if (answer === question.correctAnswer) {
          this.correctAnswers++;
        }
      });

      // Show results
      this.isComplete = true;
      this.showResults();
    });
  }
}

export default InteractiveQuiz;
```

