/**
 * e-commerce platform's database setup script
 * 
 * This script sets up the necessary tables and schema for an e-commerce platform's database.
 * It populates the categories table with dummy data and adds an index to the users table for faster searching.
 * 
 * Changelog:
 * 
 * v1.0.0 - Initial version
 */

-- Ensure compatibility with PostgreSQL 12.0
SET client_min_messages TO WARNING;
SET standard_conforming_strings = on;
SET check_function_bodies = false;
SET client_encoding = 'UTF8';
SET search_path = public, pg_catalog;
SET default_tablespace = '';
SET default_with_oids = false;

-- Define transaction to perform all operations atomically
BEGIN;
SAVEPOINT sp1;

-- Create ecommerce_data schema if it doesn't already exist
CREATE SCHEMA IF NOT EXISTS ecommerce_data;

-- Create users table
CREATE TABLE IF NOT EXISTS ecommerce_data.users (
    user_id SERIAL PRIMARY KEY,
    email VARCHAR UNIQUE NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    is_active BOOLEAN DEFAULT true
);

-- Create products table
CREATE TABLE IF NOT EXISTS ecommerce_data.products (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR NOT NULL,
    price NUMERIC NOT NULL CHECK(price > 0),
    in_stock INTEGER NOT NULL,
    category_id INTEGER REFERENCES ecommerce_data.categories(category_id)
);

-- Create orders table
CREATE TABLE IF NOT EXISTS ecommerce_data.orders (
    order_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES ecommerce_data.users(user_id),
    order_date DATE,
    total_amount NUMERIC CHECK(total_amount >= 0),
    status ENUM('placed', 'shipped', 'delivered', 'cancelled')
);

-- Create categories table
CREATE TABLE IF NOT EXISTS ecommerce_data.categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR UNIQUE NOT NULL
);

-- Populate categories table with dummy data
INSERT INTO ecommerce_data.categories (category_name) VALUES
    ('Clothing'),
    ('Electronics'),
    ('Home Goods'),
    ('Health & Beauty'),
    ('Toys & Games');

-- Add index on email column in users table for faster searching
CREATE INDEX IF NOT EXISTS users_email_idx ON ecommerce_data.users (email);

-- Rollback on errors
EXCEPTION WHEN OTHERS THEN
    ROLLBACK TO SAVEPOINT sp1;
    RAISE;
END;
COMMIT;

-- Select all active users
SELECT * FROM ecommerce_data.users WHERE is_active = true;

-- Retrieve 10 most expensive products that are in stock
SELECT * FROM ecommerce_data.products WHERE in_stock > 0 ORDER BY price DESC LIMIT 10;

-- List number of orders placed in each category
SELECT 
    ecommerce_data.categories.category_name, 
    COUNT(*) AS num_orders
FROM 
    ecommerce_data.orders 
    JOIN ecommerce_data.users ON ecommerce_data.orders.user_id = ecommerce_data.users.user_id 
    JOIN ecommerce_data.products ON ecommerce_data.orders.order_id = ecommerce_data.products.product_id 
    JOIN ecommerce_data.categories ON ecommerce_data.products.category_id = ecommerce_data.categories.category_id 
WHERE 
    ecommerce_data.orders.status IN ('placed', 'shipped')
GROUP BY 
    ecommerce_data.categories.category_name;

