package com.example.banking;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import java.sql.SQLException;

public class BankingApplication {
    private Connection connection;
    private String accountNumber;
    private String accountHolder;
    private double balance;

    public BankingApplication(String accountNumber, String accountHolder, double balance) {
        this.accountNumber = accountNumber;
        this.accountHolder = accountHolder;
        this.balance = balance;

        try {
            Class.forName("com.mysql.jdbc.Driver");
            String url = "jdbc:mysql://localhost:3306/banking";
            String user = "root";
            String password = "password";
            connection = DriverManager.getConnection(url, user, password);
            connection.setAutoCommit(false);
        } catch (ClassNotFoundException | SQLException e) {
            e.printStackTrace();
        }
    }

    public void deposit(double amount) {
        try {
            PreparedStatement statement = connection.prepareStatement("UPDATE accounts SET balance = balance + ? WHERE account_number = ?");
            statement.setDouble(1, amount);
            statement.setString(2, accountNumber);
            statement.executeUpdate();
            connection.commit();
            balance += amount;
            System.out.println("Deposit successful. New balance: " + balance);
        } catch (SQLException e) {
            e.printStackTrace();
            try {
                connection.rollback();
            } catch (SQLException ex) {
                ex.printStackTrace();
            }
        }
    }

    public void withdraw(double amount) throws InsufficientFundsException {
        if (amount > balance) {
            throw new InsufficientFundsException("Insufficient funds.");
        }

        try {
            PreparedStatement statement = connection.prepareStatement("UPDATE accounts SET balance = balance - ? WHERE account_number = ?");
            statement.setDouble(1, amount);
            statement.setString(2, accountNumber);
            int rowsUpdated = statement.executeUpdate();
            if (rowsUpdated == 0) {
                throw new SQLException("Failed to withdraw funds.");
            }
            connection.commit();
            balance -= amount;
            System.out.println("Withdrawal successful. New balance: " + balance);
        } catch (SQLException e) {
            e.printStackTrace();
            try {
                connection.rollback();
            } catch (SQLException ex) {
                ex.printStackTrace();
            }
        }
    }

    public double checkBalance() {
        try {
            PreparedStatement statement = connection.prepareStatement("SELECT balance FROM accounts WHERE account_number = ?");
            statement.setString(1, accountNumber);
            ResultSet resultSet = statement.executeQuery();
            if (resultSet.next()) {
                balance = resultSet.getDouble("balance");
            }
            return balance;
        } catch (SQLException e) {
            e.printStackTrace();
            return -1;
        }
    }

    public void transferFunds(String targetAccountNumber, double amount) {
        if (amount > balance) {
            System.out.println("Insufficient funds for transfer.");
            return;
        }

        try {
            PreparedStatement withdrawStatement = connection.prepareStatement("UPDATE accounts SET balance = balance - ? WHERE account_number = ?");
            withdrawStatement.setDouble(1, amount);
            withdrawStatement.setString(2, accountNumber);
            int rowsWithdrawn = withdrawStatement.executeUpdate();
            if (rowsWithdrawn == 0) {
                throw new SQLException("Failed to withdraw funds from source account.");
            }

            PreparedStatement depositStatement = connection.prepareStatement("UPDATE accounts SET balance = balance + ? WHERE account_number = ?");
            depositStatement.setDouble(1, amount);
            depositStatement.setString(2, targetAccountNumber);
            int rowsDeposited = depositStatement.executeUpdate();
            if (rowsDeposited == 0) {
                throw new SQLException("Failed to deposit funds to target account.");
            }

            connection.commit();
            balance -= amount;
            System.out.println("Transfer successful. New balance: " + balance);
        } catch (SQLException e) {
            e.printStackTrace();
            try {
                connection.rollback();
            } catch (SQLException ex) {
                ex.printStackTrace();
            }
        }
    }

    public static void main(String[] args) {
        BankingApplication account1 = new BankingApplication("1234567890", "John Doe", 5000.00);
        BankingApplication account2 = new BankingApplication("0987654321", "Jane Smith", 10000.00);

        System.out.println("Account 1 balance: " + account1.checkBalance());
        System.out.println("Account 2 balance: " + account2.checkBalance());

        account1.deposit(1000.00);
        System.out.println("Account 1 balance: " + account1.checkBalance());

        try {
            account1.withdraw(2000.00);
            System.out.println("Account 1 balance: " + account1.checkBalance());
        } catch (InsufficientFundsException e) {
            System.out.println(e.getMessage());
        }

        account1.transferFunds("0987654321", 3000.00);
        System.out.println("Account 1 balance: " + account1.checkBalance());
        System.out.println("Account 2 balance: " + account2.checkBalance());
    }

    static class InsufficientFundsException extends Exception {
        public InsufficientFundsException(String message) {
            super(message);
        }
    }
}
