wind = [0; 0]; % m/s

% Define time step and simulation duration
dt = 0.01; % s
tmax = 10; % s

% Compute initial velocity magnitude and angle
v0 = norm([vx0, vy0]);
theta0 = atan2(vy0, vx0);

% Compute initial position and velocity vectors
r0 = [x0; y0];
v0_vec = [vx0; vy0];

% Define functions for computing drag force and Magnus force
drag_force = @(v) -0.5*rho*Cd*pi*R^2*norm(v)*v;
magnus_force = @(v, omega) cross(m*v, omega*R);

% Initialize arrays for storing simulation data
t = 0:dt:tmax;
r = zeros(2, length(t));
r(:, 1) = r0;
v = zeros(2, length(t));
v(:, 1) = v0_vec;
a = zeros(2, length(t));
a(:, 1) = [0; -g];

% Simulation loop using Euler's method
