    % Compute air resistance and Magnus forces
    F_drag = drag_force(v(:, i-1) - wind);
    F_magnus = magnus_force(v(:, i-1), omega);

    % Compute acceleration based on forces
    a(:, i) = (F_drag + F_magnus)/m - [0; g];

    % Compute new velocity and position using Euler's method
    v(:, i) = v(:, i-1) + a(:, i)*dt;
    r(:, i) = r(:, i-1) + v(:, i)*dt;

    % Stop simulation if projectile hits the ground
    if r(2, i) <= 0
        r(:, i:end) = []; % truncate arrays
        v(:, i:end) = [];
        a(:, i:end) = [];
        t(i:end) = [];
        break
    end
