// InteractiveQuiz.js

export class InteractiveQuiz {
  constructor(config, quizData) {
    this.config = config || {};
    this.quizData = quizData || {};
    this.currentQuestionIndex = 0;
    this.correctAnswers = 0;
    this.isQuizStarted = false;

    this.init();
  }

  init() {
    this.renderQuiz();
    this.addListeners();
  }

  startQuiz() {
    this.shuffleQuestions();
    this.displayQuestion();
    this.isQuizStarted = true;
  }

  shuffleQuestions() {
    if (this.config.shuffleQuestions) {
      this.quizData.questions.sort(() => Math.random() - 0.5);
    }
  }

  displayQuestion() {
    const question = this.quizData.questions[this.currentQuestionIndex];

    let questionElement = `
      <div class="question-container">
        <h2 class="question-text">${question.questionText}</h2>
        <ul class="options-list">`;

    question.options.forEach((option, index) => {
      questionElement += `
        <li class="option">
          <input type="radio" name="question${question.id}" id="option-${index}" value="${option}">
          <label for="option-${index}">${option}</label>
        </li>`;
    });

    questionElement += `
      </ul>
      <div class="button-container">
        <button class="btn btn-primary" id="submit-button">Submit</button>
        <button class="btn btn-secondary" id="next-button" disabled>Next</button>
      </div>
    </div>`;

    const quizContainer = document.getElementById('quiz-container');
    quizContainer.innerHTML = questionElement;

    this.addListeners();
  }

  checkAnswer() {
    const selectedOption = document.querySelector(
      `input[name=question${this.quizData.questions[this.currentQuestionIndex].id}]:checked`
    );

    if (selectedOption) {
      const isCorrect =
        selectedOption.value ===
        this.quizData.questions[this.currentQuestionIndex].correctAnswer;

      if (isCorrect) {
        this.correctAnswers++;
      }

      this.showFeedback(isCorrect);
    } else {
      alert('Please select an answer before submitting.');
    }
  }

  showFeedback(isCorrect) {
    const feedbackMessage = isCorrect
      ? `<p class="feedback correct">Correct!</p>`
      : `<p class="feedback incorrect">Incorrect.</p>`;

    const submitButton = document.getElementById('submit-button');
    submitButton.insertAdjacentHTML('afterend', feedbackMessage);

    setTimeout(() => {
      submitButton.nextElementSibling.remove();
      this.enableNextButton();
    }, 1000);
  }

  enableNextButton() {
    const nextButton = document.getElementById('next-button');
    nextButton.removeAttribute('disabled');
    nextButton.focus();
  }

  nextQuestion() {
    this.currentQuestionIndex++;

    if (this.currentQuestionIndex < this.quizData.questions.length) {
      this.displayQuestion();
    } else {
      this.showResults();
    }
  }

  previousQuestion() {
    this.currentQuestionIndex--;

    if (this.currentQuestionIndex >= 0) {
      this.displayQuestion();
    }
  }

  showResults() {
    const resultsElement = `
      <div class="results-container">
        <h1>Quiz Results</h1>
        <p>You answered ${this.correctAnswers} out of ${this.quizData.questions.length} questions correctly.</p>
        <button class="btn btn-primary" id="restart-button">Restart Quiz</button>
      </div>`;

    const quizContainer = document.getElementById('quiz-container');
    quizContainer.innerHTML = resultsElement;

    this.addListeners();
  }

  addListeners() {
    const startButton = document.getElementById('start-button');
    const submitButton = document.getElementById('submit-button');
    const nextButton = document.getElementById('next-button');
    const previousButton = document.getElementById('previous-button');
    const restartButton = document.getElementById('restart-button');

    startButton.addEventListener('click', () => this.startQuiz());
    submitButton.addEventListener('click', () => this.checkAnswer());
    nextButton.addEventListener('click', () => this.nextQuestion());
    previousButton.addEventListener('click', () => this.previousQuestion());
    restartButton.addEventListener('click', () => location.reload());

    // Event delegation for ARIA roles
    const quizContainer = document.getElementById('quiz-container');
    quizContainer.addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        const activeElement = document.activeElement;

        if (activeElement === startButton) {
          this.startQuiz();
        } else if (activeElement === submitButton) {
          this.checkAnswer();
        } else if (activeElement === nextButton) {
          this.nextQuestion();
        }
      }
    });
  }

  testQuiz(answers) {
    if (!Array.isArray(answers)) {
      throw new Error('Answers must be an array.');
    }

    if (answers.length !== this.quizData.questions.length) {
      throw new Error('The number of answers must match the number of questions.');
    }

    let score = 0;
    answers.forEach((answer, index) => {
      if (answer === this.quizData.questions[index].correctAnswer) {
        score++;
      }
    });

    return score;
  }
}
