```javascript
// ui.js

// UI class to handle all UI-related functionalities
export class UI {
  constructor() {
    // Initialize the elements
    this.bookList = document.getElementById("book-list");
  }

  // Add a book to the UI
  addBookToList(book) {
    // Create a new list item element
    const li = document.createElement("li");

    // Add the book information to the list item
    li.innerHTML = `
      <span>${book.title}</span>
      <span>${book.author}</span>
      <span>${book.isbn}</span>
      <button type="button" id="${book.id}" class="delete">X</button>
    `;

    // Add the list item to the book list
    this.bookList.appendChild(li);

    // Add an event listener to the delete button
    li.querySelector(".delete").addEventListener("click", (e) => {
      this.deleteBook(e.target);
    });
  }

  // Delete a book from the UI
  deleteBook(element) {
    // Get the book ID from the delete button's ID
    const id = element.id;

    // Remove the book from the UI
    element.parentElement.remove();

    // Remove the book from the store
    store.removeBook(id);
  }

  // Clear the form fields
  clearFields() {
    document.getElementById("title").value = "";
    document.getElementById("author").value = "";
    document.getElementById("isbn").value = "";
  }

  // Render the list of books
  renderBooks(books) {
    // Clear the book list
    this.bookList.innerHTML = "";

    // Add each book to the UI
    books.forEach((book) => {
      this.addBookToList(book);
    });
  }
}
