-- Purpose: Set up the database schema and tables for an e-commerce platform named "ecommerce_data". Populate dummy data and create necessary indexes. Finally, demonstrate basic queries on the database.
-- Changelog:
-- Version 1.0.0: Initial version

-- Set the compatibility level to PostgreSQL 12
SET client_encoding TO 'UTF8';
SET standard_conforming_strings TO off;
SET check_function_bodies TO false;
SET client_min_messages TO warning;

-- Start a new transaction
BEGIN;

-- Create the "ecommerce_data" schema if it doesn't exist
CREATE SCHEMA IF NOT EXISTS ecommerce_data;

-- Create the "users" table
CREATE TABLE IF NOT EXISTS ecommerce_data.users (
    user_id SERIAL PRIMARY KEY,
    email VARCHAR(255) UNIQUE NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    is_active BOOLEAN DEFAULT TRUE
);

-- Create the "products" table
CREATE TABLE IF NOT EXISTS ecommerce_data.products (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    price NUMERIC(10,2) NOT NULL CHECK (price > 0),
    in_stock INTEGER NOT NULL,
    category_id INTEGER REFERENCES ecommerce_data.categories(category_id)
);

-- Create the "orders" table
CREATE TABLE IF NOT EXISTS ecommerce_data.orders (
    order_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES ecommerce_data.users(user_id),
    order_date DATE,
    total_amount NUMERIC(10,2) NOT NULL CHECK (total_amount >= 0),
    status ENUM('placed', 'shipped', 'delivered', 'cancelled')
);

-- Create the "categories" table
CREATE TABLE IF NOT EXISTS ecommerce_data.categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(255) UNIQUE NOT NULL
);

-- Populate the "categories" table with dummy data
INSERT INTO ecommerce_data.categories (category_name) VALUES
('Electronics'),
('Clothing'),
('Home & Kitchen'),
('Books'),
('Toys & Games');

-- Create an index on the "email" column in the "users" table
CREATE INDEX idx_users_email ON ecommerce_data.users (email);


-- Commit the transaction (if no errors occurred)
COMMIT;

-- Rollback the transaction if an error occurred
ROLLBACK;

-- Select all active users
SELECT * FROM ecommerce_data.users WHERE is_active = true;

-- Retrieve the 10 most expensive products that are in stock
SELECT * FROM ecommerce_data.products WHERE in_stock > 0 ORDER BY price DESC LIMIT 10;

-- List the number of orders placed in each category (for orders with 'placed' or 'shipped' status)
SELECT c.category_name, COUNT(o.order_id) AS order_count
FROM ecommerce_data.categories c
LEFT JOIN ecommerce_data.products p ON c.category_id = p.category_id
LEFT JOIN ecommerce_data.orders o ON p.product_id = o.order_id
WHERE o.status IN ('placed', 'shipped')
GROUP BY c.category_name;
