import java.sql.*;
import java.util.Scanner;

public class BankingApplication {

    private Connection connection;
    private String accountNumber;
    private String accountHolder;
    private double balance;

    public BankingApplication(String accountNumber, String accountHolder, double initialBalance) {
        this.accountNumber = accountNumber;
        this.accountHolder = accountHolder;
        this.balance = initialBalance;

        // Establish connection to the database
        try {
            connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/banking", "root", "password");
        } catch (SQLException e) {
            System.out.println("Error connecting to the database: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public void deposit(double amount) {
        // Update the balance in the database
        String sql = "UPDATE Accounts SET balance = balance + ? WHERE account_number = ?";
        try (PreparedStatement statement = connection.prepareStatement(sql)) {
            statement.setDouble(1, amount);
            statement.setString(2, accountNumber);
            statement.executeUpdate();

            balance += amount; // Update the balance in memory

            System.out.println("Deposited $" + amount + " successfully.");
        } catch (SQLException e) {
            System.out.println("Error depositing funds: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public void withdraw(double amount) throws InsufficientFundsException {
        // Check if sufficient funds are available
        if (amount > balance) {
            throw new InsufficientFundsException("Insufficient funds in your account.");
        }

        // Deduct the amount from the balance in the database
        String sql = "UPDATE Accounts SET balance = balance - ? WHERE account_number = ?";
        try (PreparedStatement statement = connection.prepareStatement(sql)) {
            statement.setDouble(1, amount);
            statement.setString(2, accountNumber);
            statement.executeUpdate();

            balance -= amount; // Update the balance in memory

            System.out.println("Withdrew $" + amount + " successfully.");
        } catch (SQLException e) {
            System.out.println("Error withdrawing funds: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public double checkBalance() {
        // Retrieve the current balance from the database
        String sql = "SELECT balance FROM Accounts WHERE account_number = ?";
        try (PreparedStatement statement = connection.prepareStatement(sql)) {
            statement.setString(1, accountNumber);
            ResultSet resultSet = statement.executeQuery();

            if (resultSet.next()) {
                balance = resultSet.getDouble("balance"); // Update the balance in memory
                return balance;
            }
        } catch (SQLException e) {
            System.out.println("Error checking balance: " + e.getMessage());
            e.printStackTrace();
        }
        return 0.0; // Return 0.0 in case of errors or no data found
    }

    public void transferFunds(String targetAccountNumber, double amount) throws InsufficientFundsException {
        // Check if sufficient funds are available
        if (amount > balance) {
            throw new InsufficientFundsException("Insufficient funds in your account.");
        }

        // Start a transaction
        try {
            connection.setAutoCommit(false); // Disable autocommit

            // Deduct the amount from the sender's account
            String sql1 = "UPDATE Accounts SET balance = balance - ? WHERE account_number = ?";
            try (PreparedStatement statement = connection.prepareStatement(sql1)) {
                statement.setDouble(1, amount);
                statement.setString(2, accountNumber);
                statement.executeUpdate();

                balance -= amount; // Update the balance in memory
            }

            // Add the amount to the receiver's account
            String sql2 = "UPDATE Accounts SET balance = balance + ? WHERE account_number = ?";
            try (PreparedStatement statement = connection.prepareStatement(sql2)) {
                statement.setDouble(1, amount);
                statement.setString(2, targetAccountNumber);
                statement.executeUpdate();
            }

            // Commit the transaction
            connection.commit();

            System.out.println("Transferred $" + amount + " to account " + targetAccountNumber + " successfully.");
        } catch (SQLException e) {
            // Rollback the transaction if any errors occur
            connection.rollback();
            System.out.println("Error transferring funds: " + e.getMessage());
            e.printStackTrace();
        } finally {
            // Reset autocommit to default (true)
            try {
                connection.setAutoCommit(true);
            } catch (SQLException e) {
                System.out.println("Error resetting autocommit: " + e.getMessage());
                e.printStackTrace();
            }
        }
    }

    public static void main(String[] args) {
        Scanner input = new Scanner(System.in);

        // Create an instance of BankingApplication with dummy data
        BankingApplication bankingApp = new BankingApplication("1234567890", "John Doe", 1000.0);

        // Deposit funds
        System.out.print("Enter the amount to deposit: $");
        double depositAmount = input.nextDouble();
        bankingApp.deposit(depositAmount);

        // Check the balance
        System.out.println("Current balance: $" + bankingApp.checkBalance());

        // Withdraw funds
        System.out.print("Enter the amount to withdraw: $");
        double withdrawAmount = input.nextDouble();
        try {
            bankingApp.withdraw(withdrawAmount);
        } catch (InsufficientFundsException e) {
            System.out.println(e.getMessage());
        }

        // Check the balance
        System.out.println("Current balance: $" + bankingApp.checkBalance());

        // Transfer funds to another account
        System.out.print("Enter the target account number: ");
        String targetAccountNumber = input.next();
        System.out.print("Enter the amount to transfer: $");
        double transferAmount = input.nextDouble();

        try {
            bankingApp.transferFunds(targetAccountNumber, transferAmount);
        } catch (InsufficientFundsException e) {
            System.out.println(e.getMessage());
        }

        // Check the balance
        System.out.println("Current balance: $" + bankingApp.checkBalance());

        input.close();
    }

    // Custom exception for insufficient funds
    public static class InsufficientFundsException extends Exception {

        public InsufficientFundsException(String message) {
            super(message);
        }
    }
}
```

