```c++
#include <iostream>
#include <vector>

using namespace std;

class Product
{
public:
    string itemCode;
    string name;
    double price;
    int inventoryCount;

    Product(string code, string n, double p, int count)
    {
        itemCode = code;
        name = n;
        price = p;
        inventoryCount = count;
    }
};

class VendingMachine
{
public:
    vector<Product> products;

    VendingMachine()
    {
        // Initialize the vending machine products
        products.push_back(Product("A1", "Chips", 1.50, 10));
        products.push_back(Product("B2", "Candy Bar", 0.75, 10));
        products.push_back(Product("C3", "Soda", 1.00, 10));
        products.push_back(Product("D4", "Gum", 0.50, 10));
        products.push_back(Product("E5", "Cookies", 2.50, 10));
    }

    void displayProducts()
    {
        cout << "Vending Machine Products:" << endl;
        for (Product product : products)
        {
            cout << product.itemCode << " - " << product.name << " - $" << product.price << endl;
        }
        cout << endl;
    }

    void selectProduct(string itemCode)
    {
        // Find the selected product
        Product* selectedProduct = nullptr;
        for (Product& product : products)
        {
            if (product.itemCode == itemCode)
            {
                selectedProduct = &product;
                break;
            }
        }

        // Check if the product is available
        if (selectedProduct == nullptr || selectedProduct->inventoryCount == 0)
        {
            cout << "Sorry, the product is not available." << endl;
            return;
        }

        // Prompt the user to insert money
        cout << "Please insert money to purchase the product." << endl;
        double amountInserted = 0;
        while (amountInserted < selectedProduct->price)
        {
            cout << "Enter the amount inserted ($1, $5, $10, or $25 cents): ";
            cin >> amountInserted;

            // Check if the input is a valid denomination
            if (amountInserted != 1 && amountInserted != 5 && amountInserted != 10 && amountInserted != 25)
            {
                cout << "Invalid denomination. Please insert a valid amount." << endl;
                amountInserted = 0;
            }
        }

        // Calculate the change
        double change = amountInserted - selectedProduct->price;

        // Deduct the product from the inventory
        selectedProduct->inventoryCount--;

        // Display the details to the user
        cout << "Product: " << selectedProduct->name << endl;
        cout << "Price: $" << selectedProduct->price << endl;
        cout << "Change: $" << change << endl;
        cout << endl;
    }

    bool isOutOfStock()
    {
        // Check if all products are out of stock
        for (Product product : products)
        {
            if (product.inventoryCount > 0)
            {
                return false;
            }
        }
        return true;
    }
};

int main()
{
    VendingMachine vendingMachine;

    while (!vendingMachine.isOutOfStock())
    {
        // Display the products
        vendingMachine.displayProducts();

        // Prompt the user to select a product
        string itemCode;
        cout << "Enter the item code of the product you want to purchase: ";
        cin >> itemCode;

        // Select the product
        vendingMachine.selectProduct(itemCode);
    }

    cout << "Sorry, the vending machine is out of stock. Please come back later." << endl;

    return 0;
}
