% Define initial conditions
x0 = 0;      % Initial x-position (m)
y0 = 1;      % Initial y-position (m)
v_x0 = 30;   % Initial x-velocity (m/s)
v_y0 = 40;   % Initial y-velocity (m/s)
m = 0.145;   % Mass of the projectile (kg)
g = 9.81;    % Gravitational acceleration (m/s^2)

% Define air resistance parameters
rho = 1.225;  % Air density (kg/m^3)
C_d = 0.47;   % Drag coefficient
R = 0.0366;   % Radius of the projectile (m)

% Define Magnus effect parameters
omega_x = 0;   % Angular velocity about x-axis (rad/s)
omega_y = 0;   % Angular velocity about y-axis (rad/s)
omega_z = 300; % Angular velocity about z-axis (rad/s)

% Define wind velocity
wind_x = 0;   % Wind velocity in x-direction (m/s)
wind_y = 0;   % Wind velocity in y-direction (m/s)

% Define time step
dt = 0.001;  % Time step (s)

% Time-stepping loop using the RK4 method
t = 0;              % Initialize time (s)
x = x0;             % Initialize x-position (m)
y = y0;             % Initialize y-position (m)
v_x = v_x0;         % Initialize x-velocity (m/s)
v_y = v_y0;         % Initialize y-velocity (m/s)
trajectory = [x, y]; % Initialize trajectory matrix

% Simulate until the projectile hits the ground
while y >= 0
    % Compute drag force
    drag_force = compute_drag_force(v_x, v_y, rho, C_d, R);

    % Compute Magnus force
    magnus_force = compute_magnus_force(v_x, v_y, omega_z, rho, R);

    % Compute total force
    F_x = drag_force(1) + magnus_force(1) + (wind_x - v_x) * m;
    F_y = drag_force(2) + magnus_force(2) + (wind_y - v_y) * m - m * g;

    % Compute acceleration
    a_x = F_x / m;
    a_y = F_y / m;

    % Update position and velocity using RK4 method
    k1_x = dt * v_x;
    k1_y = dt * v_y;
    k1_v_x = dt * a_x;
    k1_v_y = dt * a_y;

    k2_x = dt * (v_x + 0.5 * k1_v_x);
    k2_y = dt * (v_y + 0.5 * k1_v_y);
    k2_v_x = dt * (a_x + 0.5 * k1_a_x);
    k2_v_y = dt * (a_y + 0.5 * k1_a_y);

    k3_x = dt * (v_x + 0.5 * k2_v_x);
    k3_y = dt * (v_y + 0.5 * k2_v_y);
    k3_v_x = dt * (a_x + 0.5 * k2_a_x);
    k3_v_y = dt * (a_y + 0.5 * k2_a_y);

    k4_x = dt * (v_x + k3_v_x);
    k4_y = dt * (v_y + k3_v_y);
    k4_v_x = dt * (a_x + k3_a_x);
    k4_v_y = dt * (a_y + k3_a_y);

    v_x = v_x + (1/6) * (k1_v_x + 2*k2_v_x + 2*k3_v_x + k4_v_x);
    v_y = v_y + (1/6) * (k1_v_y + 2*k2_v_y + 2*k3_v_y + k4_v_y);
    x = x + (1/6) * (k1_x + 2*k2_x + 2*k3_x + k4_x);
    y = y + (1/6) * (k1_y + 2*k2_y + 2*k3_y + k4_y);

    % Store trajectory data
    trajectory = [trajectory; x, y];

    % Update time
    t = t + dt;
end

% Compute time of flight, maximum height, range, and final position
time_of_flight = t;
max_height = max(trajectory(:, 2));
range = trajectory(end, 1);
final_position = trajectory(end, 1:2);

% Plot trajectory and compute final velocity
final_velocity = norm([v_x, v_y]);
figure;
plot(trajectory(:, 1), trajectory(:, 2));
xlabel('x (m)');
ylabel('y (m)');
title('Projectile Trajectory');
grid on;

% Display results
fprintf('Time of Flight: %.3f seconds\n', time_of_flight);
fprintf('Maximum Height: %.3f meters\n', max_height);
fprintf('Range: %.3f meters\n', range);
fprintf('Final Position: (%.3f, %.3f) meters\n', final_position);
fprintf('Final Velocity: %.3f m/s\n', final_velocity);

% Define functions to compute drag and Magnus forces
function drag_force = compute_drag_force(v_x, v_y, rho, C_d, R)
    v = sqrt(v_x^2 + v_y^2);
    drag_force = -0.5 * rho * C_d * pi * R^2 * v * [v_x; v_y] / m;
end

function magnus_force = compute_magnus_force(v_x, v_y, omega_z, rho, R)
    v = sqrt(v_x^2 + v_y^2);
    magnus_force = 0.5 * rho * pi * R^3 * omega_z * [v_y; -v_x] / m;
end

