  }
}

// Create holographic billboards
billboard1 {
  plane {
    <0, 0, 50>, <0, 100, 50>, <100, 0, 50>
    texture {
      pigment {
        rgb <1, 1, 1>
        texture {
          image_map { moon.tif }
          animate { linear 0.5 }
        }
      }
    }
    translate <10, 50, -5>
  }
}

// Create a flying vehicle
vehicle {
  union {
    sphere {
      <0, 0, 0>, 3
      texture { rgb <0, 1, 0>}
    }
    box { <0, -3, -5>, <0, 3, 5> }
    cylinder {
      <0, 0, -5>, <0, 0, 5>, 1
      texture { rgb <1, 0, 0>}
    }
  }
  translate <20, 25, 0>
}

// Create reflective wet streets
#declare wet_street =
  pigment { color rgb <0.1, 0.1, 0.1> }
  finish {
    reflection 0.5
    specular 0.5
    phong_size 100
  }
}

// Create a body of water
water {
  plane {
    <0, 0, 0>, <100, 0, 0>, <0, 100, 0>
    texture {
      pigment { color rgb <0, 0, 0.5> }
      finish {
        reflection 0.8
        roughness 0.5
        ior 1.333
      }
    }
  }
  translate <0, -5, 0>
}

// Create lighting
