  color rgb <0.1, 0.1, 0.2>
  gradient y
    0 { color rgb <0, 0, 0> }
    1 { color rgb <0.2, 0.2, 0.3> }
}

// Add a moon and stars
moon {
  sphere {
    <0, 100, 0>, 10
    texture { image_map { moon.tif } }
    bump_map { color_map { moon_bump.tif } }
  }
}

stars {
  #declare stars_texture =
    pigment { color_map { stars.tif } }

  disk {
    <0, 0, 0>, 0.05
    texture { stars_texture }
  }
}

// Camera setup
camera {
  look_at <0, 40, 0>
  location <0, 100, 100>
  angle 60
  focal_length 10
}

render {
  quality 10
  antialias { threshold 0.1 }
}
