```javascript
// Quiz data (dummy data with at least 3 questions)
const quizData = [
  {
    id: 1,
    questionText: "What's the capital of France?",
    options: ["Paris", "London", "Berlin", "Rome"],
    correctAnswer: 0,
  },
  {
    id: 2,
    questionText: "What's 1 + 1?",
    options: ["2", "3", "4", "5"],
    correctAnswer: 0,
  },
  {
    id: 3,
    questionText: "What color is the sky?",
    options: ["Blue", "Red", "Green", "Yellow"],
    correctAnswer: 0,
  },
];

class InteractiveQuiz {
  constructor(config = { shuffleQuestions: false, autoProgress: false }) {
    this.config = config;
    this.quizData = quizData;
    this.currentQuestion = 0;
    this.answers = [];
    this.result = { correct: 0, total: 0 };

    if (this.config.shuffleQuestions) {
      const shuffledQuestions = Array.from(new Set(
        quizData.map((q) => q.id)
      )).map((id) => quizData[Math.floor(Math.random() * quizData.length)]);
      this.quizData = shuffledQuestions;
    }
  }

  startQuiz() {
    this.renderQuiz();
  }

  nextQuestion() {
    if (this.answers.length < this.quizData.length) {
      this.currentQuestion++;
      this.renderQuestion();
    }
  }

  prevQuestion() {
    if (this.answers.length > 0) {
      this.currentQuestion--;
      this.renderQuestion();
    }
  }

  checkAnswer(answerIndex) {
    const question = this.quizData[this.currentQuestion];
    const answer = question.options[answerIndex];

    if (answer === question.correctAnswer) {
      this.result.correct++;
      this.answers.push(answerIndex);
      this.nextQuestion();
    } else {
      this.result.total++;
      this.nextQuestion();
    }
  }

  renderQuiz() {
    const quizContainer = document.getElementById("quiz-container");
    quizContainer.innerHTML = this.renderQuestion();
  }

  renderQuestion() {
    const question = this.quizData[this.currentQuestion];
    return `
      <div>
        <h3>${question.questionText}</h3>
        <ul>
          ${question.options.map((option, index) => `
            <li>
              <input type="radio" name="answer" id="answer-${index + 1}" value="${index + 1}" aria-label="${option}">
              <label for="answer-${index + 1}">${option}</label>
            </li>
          `).join("")}
        </ul>
        <button onclick="this.parentNode.parentNode.nextQuestion()">Next</button>
        <button onclick="this.parentNode.parentNode.prevQuestion()">Prev</button>
        <button onclick="this.parentNode.parentNode.checkAnswer(this.value)">Submit</button>
      </div>
    `;
  }

  testQuiz(answers) {
    this.answers = answers;
    this.result.correct = 0;
    this.result.total = 0;
    this.currentQuestion = 0;
    this.renderQuiz();

    // Simulate user interactions for each question
    for (let i = 0; i < answers.length; i++) {
      this.checkAnswer(answers[i]);
    }

    return this.result;
  }
}

// Usage example
const interactiveQuiz = new InteractiveQuiz();
const quizContainer = document.getElementById("quiz-container");
interactiveQuiz.renderQuiz();

const exampleAnswers = [1, 1, 2, 2];
const testResult = interactiveQuiz.testQuiz(exampleAnswers);
console.log(testResult);
