    private static final String DB_URL = "jdbc:mysql://localhost:3306/bank_db";
    private static final String USER = "root";
    private static final String PASSWORD = "rootpassword";
    private static final String DRIVER = "com.mysql.cj.jdbc.Driver";

    private String accountNumber;
    private String accountHolder;
    private double balance;
    private Connection connection;

    public BankingApplication(String accountNumber, String accountHolder, double balance) {
        this.accountNumber = accountNumber;
        this.accountHolder = accountHolder;
        this.balance = balance;

        try {
            Class.forName(DRIVER);
            connection = DriverManager.getConnection(DB_URL, USER, PASSWORD);
        } catch (ClassNotFoundException | SQLException e) {
            e.printStackTrace();
        }
    }

    public void deposit(double amount) throws SQLException {
        try {
            PreparedStatement pstmt = connection.prepareStatement("INSERT INTO accounts (account_number, account_holder, balance) VALUES (?, ?, ?)");
            pstmt.setString(1, accountNumber);
            pstmt.setString(2, accountHolder);
            pstmt.setDouble(3, balance + amount);
            pstmt.executeUpdate();
            pstmt.close();
        } catch (SQLException e) {
            throw e;
        }
    }

    public void withdraw(double amount) throws SQLException, InsufficientFundsException {
        double currentBalance = checkBalance();

        if (currentBalance - amount < 0) {
            throw new InsufficientFundsException("Insufficient funds");
        }

        try {
            PreparedStatement pstmt = connection.prepareStatement("UPDATE accounts SET balance = ? WHERE account_number = ?");
            pstmt.setDouble(1, balance - amount);
            pstmt.setString(2, accountNumber);
            pstmt.executeUpdate();
            pstmt.close();
        } catch (SQLException e) {
            throw e;
        }
    }

    public double checkBalance() throws SQLException {
        try (PreparedStatement pstmt = connection.prepareStatement("SELECT balance FROM accounts WHERE account_number = ?")) {
            pstmt.setString(1, accountNumber);
            try (ResultSet rs = pstmt.executeQuery()) {
                if (rs.next()) {
                    return rs.getDouble("balance");
                } else {
                    throw new RuntimeException("Account not found");
                }
            }
        } catch (SQLException e) {
            throw e;
        }
    }

    public void transferFunds(String targetAccountNumber, double amount) throws SQLException, InsufficientFundsException {
        double currentBalance = checkBalance();

        if (currentBalance - amount < 0) {
            throw new InsufficientFundsException("Insufficient funds");
        }

        try {
            PreparedStatement pstmt = connection.prepareStatement("UPDATE accounts SET balance = ? WHERE account_number = ?");
            pstmt.setDouble(1, balance - amount);
            pstmt.setString(2, accountNumber);
            pstmt.executeUpdate();
            pstmt.close();

            pstmt = connection.prepareStatement("INSERT INTO account_transactions (account_number, transaction_amount, transaction_date) VALUES (?, ?, NOW())");
            pstmt.setString(1, accountNumber);
            pstmt.setDouble(2, amount);
            pstmt.executeUpdate();
            pstmt.close();

