    }) {}

void VendingMachine::addProduct(const Product &product) {
    products.push_back(product);
}

void VendingMachine::removeProduct(const int itemCode) {
    for (auto it = products.begin(); it != products.end(); ) {
        if ((*it).getItemCode() == itemCode) {
            it = products.erase(it);
        } else {
            it++;
        }
    }
}

bool VendingMachine::insertMoney(const int denomination) {
    switch (denomination) {
        case 1:
            currentBalance += 0.10;
            break;
        case 5:
            currentBalance += 0.50;
            break;
        case 10:
            currentBalance += 1.00;
            break;
        case 25:
            currentBalance += 2.50;
            break;
        default:
            return false;
    }
    return true;
}

bool VendingMachine::dispenseProduct(const int itemCode) {
    for (const auto &product : products) {
        if (product.getItemCode() == itemCode) {
            if (product.getInventory-- <= 0) {
                products.erase(
                    std::remove_if(products.begin(), products.end(),
                                   [&itemCode](const Product &p) { return p.getItemCode() == itemCode; }),
                    products.end());
            }
            return true;
        }
    }
    return false;
}

