 Here's a GLSL fragment shader that simulates a dynamic water surface with the specified requirements:
```glsl
#version 330 core

// Water texture coordinates
const vec2 waterTexCoords [= vec2(0.0, 0.0), vec2(1.0, 0.0), vec2(1.0, 1.0), vec2(0.0, 1.0)];

// Water base color
const vec3 waterColor [= vec3(0.2, 0.5, 0.8)];

// Wave parameters
const float waveAmplitude = 0.05;
const float waveSpeed = 0.2;
const vec2 waveFrequencies [= vec2(2.0, 1.0)];

// Light properties
const vec3 lightColor [= vec3(1.0, 1.0, 1.0)];
const float lightIntensity = 1.5;
const vec3 lightDirection [= vec3(0.5, -1.0, 0.0)];

// Wind speed and direction
const float windSpeed = 0.1;
const vec2 windDirection [= vec2(0.5, 0.0)];

// Fresnel effect scale factor
const float fresnelScaleFactor = 10.0;

// Noise scale factor for distortion
const float noiseScaleFactor = 2.0;

// Camera properties
const vec3 cameraPosition [= vec3(0.0, 1.5, 3.0)];
const vec3 cameraDirection [= vec3(-0.5, 0.0, 0.0)];

void main()
{
    // Calculate water texture coordinate
    vec2 waterTexCoord = waterTexCoords[gl_FragCoord.y];

    // Calculate wind-affected wave vector
    vec2 waveVector = normalize(vec2(sin(windDirection[0] * 10.0 + waveFrequencies[0] * gl_FragCoord.x), cos(windDirection[1] * 10.0 + waveFrequencies[1] * gl_FragCoord.x)));

    // Calculate wave height
    float waveHeight = waveAmplitude * sin((waveSpeed * gl_FragCoord.x) + vec2(0.0, 1.0) * waveVector);

    // Calculate view-dependent water color
    vec3 viewerDirection = normalize(cameraPosition - vec3(0.0, 0.5, 0.0));
    vec3 waterColorMix = mix(waterColor[0], viewerDirection, 0.5);

    // Calculate fresnel effect
    float fresnelFactor = max(dot(viewerDirection, normalize(waterTexCoord)), 0.0);
    waterColorMix += fresnelScaleFactor * (1.0 - fresnelFactor);

    // Calculate distortion
    vec3 distortionColor = texture(waterTexSampler, waterTexCoord + vec2(noiseScaleFactor * sin(gl_FragCoord.x * 10.0), 0.0)).xyz;

    // Add distortion color to waterColorMix based on angle of incidence
    distortionColor = mix(distortionColor, waterColorMix, 0.5);

    // Calculate final water color
    gl_FragColor.xyz = mix(vec3(1.0, 1.0, 1.0), distortionColor, 0.5);

    // Calculate lighting
    vec3 viewDir = normalize(cameraPosition - vec3(0.0, 0.5, 0.0));
    vec3 lightVec = normalize(lightDirection);
    float nDotV = max(0.0, dot(viewDir, lightVec));
    float diffuse = max(0.0, nDotV);
    gl_FragColor.xyz = mix(gl_FragColor.xyz, lightColor, diffuse);
}
