import os
import json

class LightDB:
    def __init__(self, db_file_path):
        self.db_file_path = db_file_path
        self.db = {}

    def create(self, record):
        try:
            with open(self.db_file_path, 'a') as f:
                json.dump(record, f)
                print(f"Record created: {record}")
        except Exception as e:
            print(f"Error creating record: {e}")

    def read(self, query):
        try:
            with open(self.db_file_path, 'r') as f:
                records = json.load(f)
                if query in records:
                    print(f"Record found: {query}")
                else:
                    print(f"Record not found: {query}")
        except Exception as e:
            print(f"Error reading records: {e}")

    def update(self, query, update_data):
        try:
            with open(self.db_file_path, 'r') as f:
                records = json.load(f)
                for record in records:
                    if query in record:
                        record.update(update_data)
        except Exception as e:
            print(f"Error updating records: {e}")

    def delete(self, query):
        try:
            with open(self.db_file_path, 'r') as f:
                records = json.load(f)
                records.pop(records.pop(None) is None and query or query in records)
        except Exception as e:
            print(f"Error deleting records: {e}")

    def persist(self, record):
        try:
            with open(self.db_file_path, 'a') as f:
                json.dump(record, f)
        except Exception as e:
            print(f"Error persisting record: {e}")

def main():
    db_file_path = input("Enter the path to the LightDB file: ")
    lightdb = LightDB(db_file_path)

    while True:
        operation = input("Enter 'create', 'read', 'update', or 'delete' (or 'quit' to exit): ").lower()

        if operation == 'create':
            print("Enter the data to create (as a Python dictionary): ")
            record = input().strip()
            lightdb.create(record)

        elif operation == 'read':
            print("Enter the search criteria (as a Python dictionary): ")
            query = input().strip()
            lightdb.read(query)

        elif operation == 'update':
            print("Enter the search criteria (as a Python dictionary): ")
            query = input().strip()
            print("Enter the updated data (as a Python dictionary): ")
            update_data = input().strip()
            lightdb.update(query, update_data)

        elif operation == 'delete':
            print("Enter the search criteria (as a Python dictionary): ")
            query = input().strip()
            lightdb.delete(query)

        elif operation == 'quit':
            break

        else:
            print("Invalid operation. Please try again.")

if __name__ == "__main__":
    main()
```
