struct Product {
    int itemCode;
    std::string name;
    double price;
    int inventory;
};

class VendingMachine {
public:
    VendingMachine(std::vector<Product> products) {
        for (Product& p : products) {
            p.inventory = 10;
        }
    }

    void displayProducts() {
        std::cout << "Please enter the item code of the product you want to purchase: " << std::endl;
        for (const auto& product : products) {
            std::cout << product.name << ": $" << product.price << std::endl;
        }
        std::cout << std::endl;
    }

    bool purchaseProduct(int itemCode, double amount) {
        for (const auto& product : products) {
            if (product.itemCode == itemCode) {
                if (amount >= product.price) {
                    --product.inventory;
                    return true;
                } else {
                    std::cout << "Insufficient funds. The transaction cannot be completed." << std::endl;
                    return false;
                }
            }
        }
        std::cout << "Invalid item code. Please try again." << std::endl;
        return false;
    }

    void displayChange(double amount) {
        if (amount) {
            std::cout << "Change: $" << amount << std::endl;
        } else {
            std::cout << "No change." << std::endl;
        }
    }

private:
    std::vector<Product> products;
};

